var React = require("react");
var Style = require("../react-inline-style");
///////////////////////////////////////////////////////////////////////////////

Style = Style.define({
  textComponent : {
    color: "red"
  }
});
 
///////////////////////////////////////////////////////////////////////////////
module.exports = React.createClass({
  mixins : [Style()],
  render : render
});
///////////////////////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////////////////////
function render(){
  return (
    <p className ="textComponent" style={this.style("textComponent", {textAlign:"left"})}>textComponent</p>
  );
}

